import socket

from django.core.mail import mail_admins, mail_managers, send_mail
from django.core.management.base import BaseCommand
from django.utils import timezone


class Command(BaseCommand):
    help = "Sends a test templates to the templates addresses specified as arguments."
    missing_args_message = "You must specify some templates recipients, or pass the --managers or --admin options."

    def add_arguments(self, parser):
        parser.add_argument(
            'templates', nargs='*',
            help='One or more templates addresses to send a test templates to.',
        )
        parser.add_argument(
            '--managers', action='store_true', dest='managers',
            help='Send a test templates to the addresses specified in settings.MANAGERS.',
        )
        parser.add_argument(
            '--admins', action='store_true', dest='admins',
            help='Send a test templates to the addresses specified in settings.ADMINS.',
        )

    def handle(self, *args, **kwargs):
        subject = 'Test templates from %s on %s' % (socket.gethostname(), timezone.now())

        send_mail(
            subject=subject,
            message="If you\'re reading this, it was successful.",
            from_email=None,
            recipient_list=kwargs['templates'],
        )

        if kwargs['managers']:
            mail_managers(subject, "This templates was sent to the site managers.")

        if kwargs['admins']:
            mail_admins(subject, "This templates was sent to the site admins.")
